import React, { useState } from 'react';
import { Link } from 'react-router-dom';

// Import icons
import { FaBars, FaTimes } from 'react-icons/fa';

// Import the doonies image directly
import dooniesImage from '../doonies-tp.png';

const HomePage = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // Function to handle opening the avatar builder
  const openAvatarBuilder = () => {
    window.location.href = '/dsbuilder/public/index.html';
  };

  return (
    <div className="min-h-screen bg-black text-white flex flex-col">
      {/* Header */}
      <header className="py-6 relative">
        <button 
          onClick={toggleSidebar}
          className="absolute left-6 top-6 text-white text-2xl focus:outline-none z-20"
        >
          {sidebarOpen ? <FaTimes /> : <FaBars />}
        </button>
        <h1 className="text-center text-5xl md:text-7xl font-bold tracking-wider">POSER</h1>
      </header>

      {/* Sidebar */}
      <div
        className={`fixed top-0 left-0 h-full w-20 bg-gray-900 z-10 sidebar-transition ${
          sidebarOpen ? 'transform translate-x-0' : 'transform -translate-x-20'
        }`}
      >
        <div className="pt-24 flex flex-col items-center space-y-8">
          <button 
            onClick={openAvatarBuilder}
            className="p-2 rounded-lg hover:bg-gray-800 transition-all duration-300 transform hover:scale-110 hover:shadow-glow bg-transparent border-0 cursor-pointer"
          >
            <div className="flex flex-col items-center">
              <img 
                src={dooniesImage} 
                alt="Doonies" 
                className="w-12 h-12 object-contain"
              />
              <span className="text-xs mt-1">Doonies</span>
            </div>
          </button>
        </div>
      </div>

      {/* Main content */}
      <main className="flex-1 flex flex-col items-center justify-center px-4">
        <div className="max-w-4xl w-full text-center">
          <h2 className="text-3xl md:text-4xl mb-6 text-purple-300 font-semibold">Create Your Digital Avatar</h2>
          <p className="text-lg md:text-xl mb-12 text-gray-300">
            Design and customize unique characters with our intuitive builder.
          </p>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div className="bg-gray-900 bg-opacity-50 p-8 rounded-xl">
              <h3 className="text-2xl mb-4 text-pink-300">Express Yourself</h3>
              <p className="text-gray-400">
                Customize every detail of your avatar with our extensive options.
              </p>
            </div>
            <div className="bg-gray-900 bg-opacity-50 p-8 rounded-xl">
              <h3 className="text-2xl mb-4 text-blue-300">Multiple Styles</h3>
              <p className="text-gray-400">
                Choose from various avatar styles including Doonies with more coming soon.
              </p>
            </div>
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="py-6 text-center text-gray-500 text-sm">
        <p>© 2023 POSER Avatar Builder. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
